mapper.select:
select * from feed
where maked_uid = #{maked_uid}
order by register_date desc

select f.*, u.user_nickname, u.user_img from feed f left outer join user u
on f.maked_uid = u.uid
where f.feed_id = #{feed_id}

select * from feed
where challenge_id = #{challenge_id}
order by register_date desc

mapper.update:
update feed
set like_count = like_count + 1
where feed_id = #{feed_id}

update feed
set like_count = like_count - 1
where feed_id = #{feed_id}

update feed
set feed_picture = #{feed_picture}
where feed_id = #{feed_id}

mapper.select:
select f.*, c.challenge_title from feed f left outer join challenge c on f.challenge_id = c.challenge_id where c.challenge_id in
#{item}
order by register_date desc
